import '../../App.css';
import { Container, Row, Col } from 'react-bootstrap';



function ScoreAndLives() {
  let lives = 3;
  let score = 0;


  return (
    <div className="ScoreAndLives">
      <Container >
        <Row>
          <Col>
              Vidas: {lives}
          </Col>
          <Col>
              Puntos: {score}
          </Col>
        </Row>
      </Container>
    </div>
  );
}

export default ScoreAndLives;
