import '../../App.css';
import { ToastContainer, toast } from 'react-toastify';
  import 'react-toastify/dist/ReactToastify.css';

function EnterTheMovie(){
    document.body.style = "background: linear-gradient(90deg, rgba("+255+","+255+","+255+","+1+") 0%, rgba("+200+","+200+","+200+","+1+") 100%);  color: white;      ";
    const notify = () => toast("Wow so easy!");

    const movies = [
        {id:1, emojis:'🐼🐍🐯🦗🐵🦩', name:'kung fu panda'},
        {id:2, emojis:'🧜‍♀️🐠🦞', name:'la sirenita'},
        {id:3, emojis:'🎮👩🏼👩🏼🤺🤺👩‍👧👨‍👧', name:'juego de gemelas'},
        {id:4, emojis:'🧊🛳👫🏼🌊🏊🏼🏊🏼‍♀️🥶🥶💀💀', name:'titanic'},
        {id:5, emojis:'🐭➡🧑🏼‍🍳🥙', name:'ratatouille'},
        {id:6, emojis:'❤🐶🍜🐶❤', name:'la dama y el vagabundo'}
      ]

      let inputValue = "";
      let xclassName = "hide";

      function myFunction() {

        xclassName = "show";

        // After 3 seconds, remove the show class from DIV
        setTimeout(function(){ xclassName = xclassName.replace("show", ""); }, 3000);
      }

    return(
        <div className="EnterTheMovie">
            <div className="card">
                <div className="container">
                    <h1><b>Adivina la película</b></h1>
                    <div className="Emojis">{movies[0].emojis}</div>
                    <input className="input" placeholder="Nombre de la película..." value={inputValue}></input>
                </div>
                <button className="btnSendMovie" >Enviar</button>

            </div>        
            <ToastContainer/>     
        </div> 
    );
}

export default EnterTheMovie;
