    const movies = [
        {id:1, emojis:'üêºüêçüêØü¶óüêµü¶©', name:'kung fu panda'},
        {id:2, emojis:'üßú‚Äç‚ôÄÔ∏èüê†ü¶û', name:'la sirenita'},
