        {id:4, emojis:'üßäüõ≥üë´üèºüåäüèäüèºüèäüèº‚Äç‚ôÄÔ∏èü•∂ü•∂üíÄüíÄ', name:'titanic'},
        {id:5, emojis:'üê≠‚û°üßëüèº‚Äçüç≥ü•ô', name:'ratatouille'},
        {id:6, emojis:'‚ù§üê∂üçúüê∂‚ù§', name:'la dama y el vagabundo'}
      ]

      let inputValue = "";
      let lives = 3;
      let score = 0;

      const notify = () => toast.error("Una vida menos, te quedan ");
      const checkMovie = () =>{

        notify();
      }

    return(

        <div className="EnterTheMovie">
            <div className="ScoreAndLives">
            <div className="font-face-Encode">
             Vidas: {lives}
            </div>
            <div>
            Puntos: {score}
            </div>
        </div>
            <div className="card">
                <div className="container">
                    <h1><b>Adivina la pel√≠cula</b></h1>
                    <div className="Emojis">{movies[0].emojis}</div>
                    <input className="input" placeholder="Nombre de la pel√≠cula..." value={inputValue}></input>
                </div>
                <button className="btnSendMovie"  onClick={checkMovie}>Enviar</button>

            </div>        
            <ToastContainer/>     
        </div> 
    );
}

export default EnterTheMovie;
